import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Appointment } from 'src/app/model/appointment';

import { AppointmentService } from 'src/app/service/appointment.service';

@Component({
  selector: 'app-update-appointment',
  templateUrl: './update-appointment.component.html',
  styleUrls: ['./update-appointment.component.css']
})
export class UpdateAppointmentComponent implements OnInit {

  id: number;
  appointment: Appointment = new Appointment();
  constructor(private appointmentService: AppointmentService, private route: ActivatedRoute, private router:Router) { }

  ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];
    this.appointmentService.getAppointmentById(this.id).subscribe(
      data => {
        this.appointment=data;
      }, error => console.log(error)
    );
  }

  saveAppointment(){
    this.appointmentService.updateAppointment(this.id, this.appointment).subscribe(data =>{
        this.goToAppointmentList();
    }, error => console.log(error));
  }

  goToAppointmentList(){
    this.router.navigate(['/landing-page/appointment-list'])
  }

}

<div class="col-md-6 offset-md-3 " style="margin-bottom: 100px;">
  <hr><br>
  <h3 class="text-center"><u>Update Appointment</u></h3>
  <form (ngSubmit) = "saveAppointment()" >

    <div class="form-group">
      <label>Appointment Id</label>
      <input type="text" class="form-control" id="appointmentId"
        [(ngModel)] = "appointment.appointmentId" name="appointmentId">
    </div>

    <div class="form-group">
      <label>Patient Id</label>
      <input type="text" class="form-control" id="patientId"
        [(ngModel)] = "appointment.patient.patientId" name="patientId">
    </div>
    <div class="form-group">
      <label>Doctor Id</label>
      <input type="text" class="form-control" id="doctorId"
        [(ngModel)] = "appointment.doctor.doctorId" name="doctorId">
    </div>
    <div class="form-group">
      <label>Appointment Date</label>
      <input type="date" class="form-control" id="appointmentDate"
        [(ngModel)] = "appointment.appointmentDate" name="appointmentDate">
    </div>
    <div class="form-group">
      <label>Appointment Status</label>
      <input type="text" class="form-control" id="appointmentStatus"
        [(ngModel)] = "appointment.appointmentStatus" name="appointmentStatus">
    </div>

    <div class="form-group">
      <label>Remark</label>
      <input type="text" class="form-control" id="remark"
        [(ngModel)] = "appointment.remark" name="remark">
    </div>
    <div class="text-center">
      <button class="btn btn-success " type="submit">Update</button>
    </div>
</form>
</div>
