import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Appointment } from 'src/app/model/appointment';
import { AppointmentService } from 'src/app/service/appointment.service';

@Component({
  selector: 'app-appointment-list',
  templateUrl: './appointment-list.component.html',
  styleUrls: ['./appointment-list.component.css']
})
export class AppointmentListComponent implements OnInit {

  appointments: Appointment[];

  constructor(private appointmentService: AppointmentService, private router: Router) { }

  ngOnInit(): void {
    this.getAppointments();
  }

  getAppointments(){
    this.appointmentService.getAppointmentList().subscribe(data => {
      this.appointments = data;
    });
  }

  updateAppointment(id: number){
    console.log(id);
    this.router.navigate(['/landing-page/update-appointment', id]);
  }



  deleteAppointment(id: number){
    // console.log(id);
    // this.router.navigate(['/landing-page/delete-user', id]);
    this.appointmentService.deleteAppointment(id).subscribe(
      data => {
        console.log(data);
        this.getAppointments();
      }, error => {
        console.log("Not Delete")
      }
    )

}
}
<div class="container">

  <div class="row justify-content-center mt-5">
    <h1> <u>Appointment List</u> </h1>
  </div>
  <button routerLink="/landing-page/create-appointment" class="btn btn-primary ml-auto ml-5 mb-3">
    Add Appointment
  </button>
<table class="table table-striped " style="margin-bottom: 100px;">
  <thead>
    <tr>
      <th>Appointment Id</th>
      <th>Patient Id</th>
      <th>Doctor Id</th>
      <th>Appointment Date</th>
      <th>Appointment Status</th>
      <th>Remark</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let appointment of appointments">
      <td>{{appointment.appointmentId}}</td>
      <td>{{appointment.patient.patientId}}</td>
      <td>{{appointment.doctor.doctorId}}</td>
      <td>{{appointment.appointmentDate}}</td>
      <td>{{appointment.appointmentStatus}}</td>
      <td>{{appointment.remark}}</td>


      <td>
        <button button (click) = "updateAppointment(appointment.appointmentId)" class="btn btn-info" >Update</button>
        <button button (click) = "deleteAppointment(appointment.appointmentId)" class="btn btn-danger" style="margin-left: 2px;" >Delete</button>
      </td>
    </tr>
  </tbody>
</table>

</div>
