import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Feedback } from 'src/app/model/feedback';
import { FeedbackService } from 'src/app/service/feedback.service';

@Component({
  selector: 'app-feedback-list',
  templateUrl: './feedback-list.component.html',
  styleUrls: ['./feedback-list.component.css']
})
export class FeedbackListComponent implements OnInit {

  feedbacks: Feedback[];


  constructor(private feedbackService: FeedbackService, private router: Router) { }

  ngOnInit(): void {
    this.getFeedbacks();
  }

  getFeedbacks(){
    this.feedbackService.getFeedbackList().subscribe(data => {
      this.feedbacks = data;
    });
  }

  updateFeedback(id: number){
    console.log(id);
    this.router.navigate(['/landing-page/update-feedback', id]);
  }



  deleteFeedback(id: number){
    // console.log(id);
    // this.router.navigate(['/landing-page/delete-user', id]);
    this.feedbackService.deleteFeedback(id).subscribe(
      data => {
        console.log(data);
        this.getFeedbacks();
      }, error => {
        console.log("Not Delete")
      }
    )

}

}
<div class="container">

  <div class="row justify-content-center mt-5">
    <h1> <u>Feedback List</u> </h1>
  </div>
  <button routerLink="/landing-page/create-feedback" class="btn btn-primary ml-auto ml-5 mb-3">
    Add Feedback
  </button>
<table class="table table-striped " style="margin-bottom: 100px;">
  <thead>
    <tr>
      <th>Feedback Id</th>
      <th>Rating</th>
      <th>Patient Id</th>
      <th>Doctor Id</th>
      <th>Feedback Comment</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let feedback of feedbacks">
      <td>{{feedback.feedbackId}}</td>
      <td>{{feedback.rating}}</td>
      <td>{{feedback.patient.patientId}}</td>
      <td>{{feedback.doctor.doctorId}}</td>
      <td>{{feedback.feedbackComment}}</td>



      <td>
        <button button (click) = "updateFeedback(feedback.feedbackId)" class="btn btn-info" >Update</button>
        <button button (click) = "deleteFeedback(feedback.feedbackId)" class="btn btn-danger" style="margin-left: 2px;" >Delete</button>
      </td>
    </tr>
  </tbody>
</table>

</div>
