import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { User } from 'src/app/model/user';
import { UserService } from 'src/app/service/user.service';

@Component({
  selector: 'app-update-user',
  templateUrl: './update-user.component.html',
  styleUrls: ['./update-user.component.css']
})
export class UpdateUserComponent implements OnInit {

  id: number;
  user: User=new User();


  constructor(private userServive: UserService, private route: ActivatedRoute, private router:Router) { }

  ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];
    this.userServive.getUserById(this.id).subscribe(
      data => {
        this.user=data;
      }, error => console.log(error)
    );
  }




  saveUser(){
    this.userServive.updateUser(this.id, this.user).subscribe(data =>{
        this.goToUserList();
    }, error => console.log(error));
  }

  goToUserList(){
    this.router.navigate(['/landing-page/user-list'])
  }

}
<div class="col-md-6 offset-md-3 " style="margin-bottom: 100px;">
  <hr><br>
  <h3 class="text-center"><u>Update User</u></h3>
  <form (ngSubmit) = "saveUser()" >

    <div class="form-group">
      <label>User Id</label>
      <input type="text" class="form-control" id="userId"
        [(ngModel)] = "user.userId" name="userId">
    </div>

    <div class="form-group">
      <label>User Name</label>
      <input type="text" class="form-control" id="userName"
        [(ngModel)] = "user.userName" name="userName">
    </div>

    <div class="form-group">
      <label>Password</label>
      <input type="password" class="form-control" id="password"
        [(ngModel)] = "user.password" name="password">
    </div>
    <div class="form-group">
      <label>Role</label>
      <input type="text" class="form-control" id="role"
        [(ngModel)] = "user.role" name="role">
    </div>

    <div class="text-center">
      <button class="btn btn-success " type="submit">Update</button>
    </div>




  </form>

</div>
